/* Reset and Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, sans-serif;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    min-height: 100vh;
    color: #333;
    line-height: 1.6;
}

.container {
    max-width: 800px;
    margin: 0 auto;
    padding: 20px;
    min-height: 100vh;
    display: flex;
    flex-direction: column;
}

/* Header Styles */
.header {
    text-align: center;
    margin-bottom: 30px;
}

.title {
    color: white;
    font-size: 2.5rem;
    font-weight: 700;
    margin-bottom: 10px;
    text-shadow: 0 2px 4px rgba(0,0,0,0.3);
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 15px;
}

.radio-icon {
    font-size: 2rem;
    animation: pulse 2s infinite;
}

@keyframes pulse {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.1); }
}

.subtitle {
    color: rgba(255,255,255,0.9);
    font-size: 1.1rem;
    font-weight: 300;
}

/* Main Content */
.main-content {
    flex: 1;
    background: white;
    border-radius: 20px;
    padding: 30px;
    box-shadow: 0 20px 40px rgba(0,0,0,0.1);
    margin-bottom: 20px;
}

/* Current Stream Section */
.current-stream {
    background: linear-gradient(135deg, #f8f9ff 0%, #e3f2fd 100%);
    border-radius: 15px;
    padding: 25px;
    margin-bottom: 30px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-wrap: wrap;
    gap: 15px;
}

.stream-info {
    flex: 1;
    min-width: 200px;
}

.stream-name {
    font-size: 1.3rem;
    font-weight: 600;
    color: #2c3e50;
    margin-bottom: 5px;
}

.stream-url {
    font-size: 0.9rem;
    color: #7f8c8d;
    word-break: break-all;
}

.status-indicator {
    display: flex;
    align-items: center;
    gap: 8px;
    flex-shrink: 0;
}

.status-dot {
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background: #95a5a6;
    transition: all 0.3s ease;
}

.status-dot.playing {
    background: #27ae60;
    animation: pulse 1.5s infinite;
}

.status-dot.error {
    background: #e74c3c;
}

.status-text {
    font-weight: 500;
    color: #2c3e50;
}

/* Controls Section */
.controls {
    margin-bottom: 30px;
}

.play-controls {
    display: flex;
    gap: 15px;
    margin-bottom: 20px;
    justify-content: center;
    flex-wrap: wrap;
}

.control-btn {
    display: flex;
    align-items: center;
    gap: 8px;
    padding: 12px 24px;
    border: none;
    border-radius: 12px;
    font-size: 1rem;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.3s ease;
    min-width: 120px;
    justify-content: center;
}

.play-pause-btn {
    background: linear-gradient(135deg, #3498db, #2980b9);
    color: white;
}

.play-pause-btn:hover:not(:disabled) {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(52, 152, 219, 0.4);
}

.stop-btn {
    background: linear-gradient(135deg, #e74c3c, #c0392b);
    color: white;
}

.stop-btn:hover:not(:disabled) {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(231, 76, 60, 0.4);
}

.control-btn:disabled {
    opacity: 0.5;
    cursor: not-allowed;
    transform: none;
}

.btn-icon {
    font-size: 1.1rem;
}

/* Volume Control */
.volume-control {
    display: flex;
    align-items: center;
    gap: 15px;
    max-width: 400px;
    margin: 0 auto;
    padding: 15px 20px;
    background: #f8f9fa;
    border-radius: 12px;
}

.volume-label {
    font-size: 1.2rem;
    flex-shrink: 0;
}

.volume-slider {
    flex: 1;
    height: 6px;
    border-radius: 3px;
    background: #ddd;
    outline: none;
    -webkit-appearance: none;
}

.volume-slider::-webkit-slider-thumb {
    -webkit-appearance: none;
    width: 20px;
    height: 20px;
    border-radius: 50%;
    background: #3498db;
    cursor: pointer;
    transition: all 0.3s ease;
}

.volume-slider::-webkit-slider-thumb:hover {
    transform: scale(1.2);
    box-shadow: 0 3px 10px rgba(52, 152, 219, 0.4);
}

.volume-slider::-moz-range-thumb {
    width: 20px;
    height: 20px;
    border-radius: 50%;
    background: #3498db;
    cursor: pointer;
    border: none;
}

.volume-value {
    font-weight: 500;
    color: #2c3e50;
    min-width: 40px;
    text-align: right;
}

/* Stream Management */
.stream-management {
    border-top: 2px solid #ecf0f1;
    padding-top: 30px;
}

.section-title {
    font-size: 1.5rem;
    font-weight: 600;
    color: #2c3e50;
    margin-bottom: 20px;
    text-align: center;
}

.subsection-title {
    font-size: 1.2rem;
    font-weight: 500;
    color: #34495e;
    margin-bottom: 15px;
}

/* Add Stream Form */
.add-stream {
    background: #f8f9fa;
    border-radius: 12px;
    padding: 20px;
    margin-bottom: 30px;
}

.stream-form {
    display: grid;
    gap: 15px;
}

.form-group {
    display: flex;
    flex-direction: column;
    gap: 5px;
}

.form-label {
    font-weight: 500;
    color: #2c3e50;
}

.form-input {
    padding: 12px 15px;
    border: 2px solid #e9ecef;
    border-radius: 8px;
    font-size: 1rem;
    transition: border-color 0.3s ease;
}

.form-input:focus {
    outline: none;
    border-color: #3498db;
    box-shadow: 0 0 0 3px rgba(52, 152, 219, 0.1);
}

.add-btn {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 8px;
    padding: 12px 20px;
    background: linear-gradient(135deg, #27ae60, #229954);
    color: white;
    border: none;
    border-radius: 8px;
    font-size: 1rem;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.3s ease;
}

.add-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(39, 174, 96, 0.4);
}

/* Stream List */
.stream-list {
    background: #f8f9fa;
    border-radius: 12px;
    padding: 20px;
}

.streams-container {
    display: grid;
    gap: 10px;
}

.stream-item {
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 15px;
    background: white;
    border-radius: 8px;
    border: 2px solid transparent;
    cursor: pointer;
    transition: all 0.3s ease;
    flex-wrap: wrap;
    gap: 10px;
}

.stream-item:hover {
    border-color: #3498db;
    transform: translateY(-1px);
    box-shadow: 0 3px 10px rgba(0,0,0,0.1);
}

.stream-item.active {
    border-color: #27ae60;
    background: linear-gradient(135deg, #d5f4e6, #f0f9ff);
}

.stream-details {
    flex: 1;
    min-width: 200px;
}

.stream-item-name {
    font-weight: 600;
    color: #2c3e50;
    margin-bottom: 2px;
}

.stream-item-url {
    font-size: 0.85rem;
    color: #7f8c8d;
    word-break: break-all;
}

.stream-actions {
    display: flex;
    gap: 8px;
    flex-shrink: 0;
}

.action-btn {
    padding: 8px 12px;
    border: none;
    border-radius: 6px;
    cursor: pointer;
    font-size: 0.9rem;
    transition: all 0.3s ease;
}

.select-btn {
    background: #3498db;
    color: white;
}

.select-btn:hover {
    background: #2980b9;
}

.delete-btn {
    background: #e74c3c;
    color: white;
}

.delete-btn:hover {
    background: #c0392b;
}

/* Empty State */
.empty-streams {
    text-align: center;
    padding: 40px 20px;
    color: #7f8c8d;
}

.empty-text {
    font-size: 1.1rem;
    margin-bottom: 5px;
}

.empty-subtext {
    font-size: 0.9rem;
}

/* Error Message */
.error-message {
    position: fixed;
    top: 20px;
    left: 50%;
    transform: translateX(-50%);
    max-width: 500px;
    width: 90%;
    z-index: 1000;
    animation: slideDown 0.3s ease-out;
}

@keyframes slideDown {
    from {
        transform: translateX(-50%) translateY(-100%);
        opacity: 0;
    }
    to {
        transform: translateX(-50%) translateY(0);
        opacity: 1;
    }
}

.error-content {
    background: #e74c3c;
    color: white;
    padding: 15px 20px;
    border-radius: 8px;
    display: flex;
    align-items: center;
    gap: 10px;
    box-shadow: 0 5px 15px rgba(0,0,0,0.2);
}

.error-icon {
    font-size: 1.2rem;
    flex-shrink: 0;
}

.error-text {
    flex: 1;
    font-weight: 500;
}

.error-close {
    background: none;
    border: none;
    color: white;
    font-size: 1.5rem;
    cursor: pointer;
    padding: 0;
    width: 25px;
    height: 25px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 50%;
    transition: background 0.3s ease;
}

.error-close:hover {
    background: rgba(255,255,255,0.2);
}

/* Footer */
.footer {
    text-align: center;
    color: rgba(255,255,255,0.8);
    font-size: 0.9rem;
    margin-top: auto;
}

/* Responsive Design */
@media (max-width: 768px) {
    .container {
        padding: 15px;
    }

    .title {
        font-size: 2rem;
        flex-direction: column;
        gap: 10px;
    }

    .main-content {
        padding: 20px;
    }

    .current-stream {
        flex-direction: column;
        align-items: stretch;
        text-align: center;
    }

    .play-controls {
        flex-direction: column;
        align-items: center;
    }

    .control-btn {
        width: 100%;
        max-width: 200px;
    }

    .volume-control {
        flex-direction: column;
        gap: 10px;
        text-align: center;
    }

    .stream-item {
        flex-direction: column;
        align-items: stretch;
        text-align: center;
    }

    .stream-actions {
        justify-content: center;
    }
}

@media (max-width: 480px) {
    .title {
        font-size: 1.6rem;
    }

    .main-content {
        padding: 15px;
        border-radius: 15px;
    }

    .current-stream,
    .add-stream,
    .stream-list {
        padding: 15px;
    }

    .form-input {
        padding: 10px 12px;
    }
}

/**
 * Radio Stream Player - JavaScript functionality
 * Handles audio playback, stream management, and local storage
 */

class RadioPlayer {
    constructor() {
        // DOM elements
        this.audioPlayer = document.getElementById('audioPlayer');
        this.playPauseBtn = document.getElementById('playPauseBtn');
        this.stopBtn = document.getElementById('stopBtn');
        this.volumeSlider = document.getElementById('volumeSlider');
        this.volumeValue = document.getElementById('volumeValue');
        this.streamForm = document.getElementById('streamForm');
        this.streamsContainer = document.getElementById('streamsContainer');
        this.currentStreamName = document.getElementById('currentStreamName');
        this.currentStreamUrl = document.getElementById('currentStreamUrl');
        this.statusDot = document.getElementById('statusDot');
        this.statusText = document.getElementById('statusText');
        this.errorMessage = document.getElementById('errorMessage');
        this.errorText = document.getElementById('errorText');
        this.errorClose = document.getElementById('errorClose');

        // State
        this.streams = [];
        this.currentStream = null;
        this.isPlaying = false;
        this.isLoading = false;

        // Storage key
        this.storageKey = 'radioStreamPlayer_streams';

        // Initialize
        this.init();
    }

    /**
     * Initialize the radio player
     */
    init() {
        this.loadStreams();
        this.setupEventListeners();
        this.setupAudioEvents();
        this.updateUI();
        this.setInitialVolume();

        console.log('Radio Player initialized');
    }

    /**
     * Setup event listeners for UI controls
     */
    setupEventListeners() {
        // Play/pause button
        this.playPauseBtn.addEventListener('click', () => this.togglePlayPause());

        // Stop button
        this.stopBtn.addEventListener('click', () => this.stopStream());

        // Volume control
        this.volumeSlider.addEventListener('input', (e) => this.updateVolume(e.target.value));

        // Stream form submission
        this.streamForm.addEventListener('submit', (e) => this.handleStreamSubmission(e));

        // Error message close
        this.errorClose.addEventListener('click', () => this.hideError());

        // Keyboard shortcuts
        document.addEventListener('keydown', (e) => this.handleKeyPress(e));

        // Page visibility change (pause when tab is hidden)
        document.addEventListener('visibilitychange', () => this.handleVisibilityChange());
    }

    /**
     * Setup audio element event listeners
     */
    setupAudioEvents() {
        // Audio can start playing
        this.audioPlayer.addEventListener('canplay', () => {
            this.isLoading = false;
            this.updateStatus('ready', 'Ready to play');
        });

        // Audio starts playing
        this.audioPlayer.addEventListener('play', () => {
            this.isPlaying = true;
            this.updatePlayButton();
            this.updateStatus('playing', 'Playing');
        });

        // Audio paused
        this.audioPlayer.addEventListener('pause', () => {
            this.isPlaying = false;
            this.updatePlayButton();
            this.updateStatus('paused', 'Paused');
        });

        // Audio ended (shouldn't happen with streams, but just in case)
        this.audioPlayer.addEventListener('ended', () => {
            this.isPlaying = false;
            this.updatePlayButton();
            this.updateStatus('ended', 'Stream ended');
        });

        // Audio loading starts
        this.audioPlayer.addEventListener('loadstart', () => {
            this.isLoading = true;
            this.updateStatus('loading', 'Connecting...');
        });

        // Audio is waiting for data
        this.audioPlayer.addEventListener('waiting', () => {
            this.updateStatus('loading', 'Buffering...');
        });

        // Audio error occurred
        this.audioPlayer.addEventListener('error', (e) => {
            this.handleAudioError(e);
        });

        // Volume changed
        this.audioPlayer.addEventListener('volumechange', () => {
            this.updateVolumeDisplay();
        });
    }

    /**
     * Handle keyboard shortcuts
     */
    handleKeyPress(e) {
        // Only handle shortcuts when not typing in input fields
        if (e.target.tagName === 'INPUT') return;

        switch(e.code) {
            case 'Space':
                e.preventDefault();
                this.togglePlayPause();
                break;
            case 'KeyS':
                e.preventDefault();
                this.stopStream();
                break;
            case 'ArrowUp':
                e.preventDefault();
                this.adjustVolume(10);
                break;
            case 'ArrowDown':
                e.preventDefault();
                this.adjustVolume(-10);
                break;
        }
    }

    /**
     * Handle page visibility change
     */
    handleVisibilityChange() {
        // Optional: pause when tab becomes hidden
        // Uncomment the next line if you want this behavior
        // if (document.hidden && this.isPlaying) this.pauseStream();
    }

    /**
     * Set initial volume
     */
    setInitialVolume() {
        const savedVolume = localStorage.getItem('radioPlayer_volume') || 50;
        this.volumeSlider.value = savedVolume;
        this.updateVolume(savedVolume);
    }

    /**
     * Load streams from localStorage
     */
    loadStreams() {
        try {
            const saved = localStorage.getItem(this.storageKey);
            this.streams = saved ? JSON.parse(saved) : this.getDefaultStreams();
            console.log(`Loaded ${this.streams.length} streams from storage`);
        } catch (error) {
            console.error('Error loading streams:', error);
            this.streams = this.getDefaultStreams();
            this.showError('Error loading saved streams. Using defaults.');
        }
    }

    /**
     * Get default sample streams
     */
    getDefaultStreams() {
        return [
            {
                id: 'sample1',
                name: 'BBC Radio 1',
                url: 'http://stream.live.vc.bbcmedia.co.uk/bbc_radio_one'
            },
            {
                id: 'sample2',
                name: 'Jazz Radio',
                url: 'http://jazz-wr11.ice.infomaniak.ch/jazz-wr11-128.mp3'
            }
        ];
    }

    /**
     * Save streams to localStorage
     */
    saveStreams() {
        try {
            localStorage.setItem(this.storageKey, JSON.stringify(this.streams));
            console.log('Streams saved to storage');
        } catch (error) {
            console.error('Error saving streams:', error);
            this.showError('Error saving streams to local storage.');
        }
    }

    /**
     * Handle stream form submission
     */
    handleStreamSubmission(e) {
        e.preventDefault();

        const nameInput = document.getElementById('streamName');
        const urlInput = document.getElementById('streamUrl');

        const name = nameInput.value.trim();
        const url = urlInput.value.trim();

        if (!name || !url) {
            this.showError('Please enter both stream name and URL.');
            return;
        }

        // Validate URL format
        if (!this.isValidStreamUrl(url)) {
            this.showError('Please enter a valid stream URL (http:// or https://).');
            return;
        }

        // Check for duplicate names
        if (this.streams.some(stream => stream.name.toLowerCase() === name.toLowerCase())) {
            this.showError('A stream with this name already exists.');
            return;
        }

        // Add new stream
        const newStream = {
            id: Date.now().toString(),
            name: name,
            url: url
        };

        this.streams.push(newStream);
        this.saveStreams();
        this.renderStreams();

        // Clear form
        nameInput.value = '';
        urlInput.value = '';

        this.showSuccess(`Stream "${name}" added successfully!`);
    }

    /**
     * Validate stream URL
     */
    isValidStreamUrl(url) {
        try {
            const urlObj = new URL(url);
            return urlObj.protocol === 'http:' || urlObj.protocol === 'https:';
        } catch {
            return false;
        }
    }

    /**
     * Render streams list
     */
    renderStreams() {
        if (this.streams.length === 0) {
            this.streamsContainer.innerHTML = `
                <div class="empty-streams">
                    <p class="empty-text">No streams saved yet</p>
                    <p class="empty-subtext">Add your first stream above to get started</p>
                </div>
            `;
            return;
        }

        this.streamsContainer.innerHTML = this.streams
            .map(stream => this.createStreamItemHTML(stream))
            .join('');

        // Add event listeners to stream items
        this.streams.forEach(stream => {
            const selectBtn = document.getElementById(`select-${stream.id}`);
            const deleteBtn = document.getElementById(`delete-${stream.id}`);

            selectBtn.addEventListener('click', () => this.selectStream(stream));
            deleteBtn.addEventListener('click', () => this.deleteStream(stream.id));
        });
    }

    /**
     * Create HTML for a stream item
     */
    createStreamItemHTML(stream) {
        const isActive = this.currentStream && this.currentStream.id === stream.id;

        return `
            <div class="stream-item ${isActive ? 'active' : ''}" data-stream-id="${stream.id}">
                <div class="stream-details">
                    <div class="stream-item-name">${this.escapeHtml(stream.name)}</div>
                    <div class="stream-item-url">${this.escapeHtml(stream.url)}</div>
                </div>
                <div class="stream-actions">
                    <button class="action-btn select-btn" id="select-${stream.id}">
                        ${isActive ? 'Selected' : 'Select'}
                    </button>
                    <button class="action-btn delete-btn" id="delete-${stream.id}">Delete</button>
                </div>
            </div>
        `;
    }

    /**
     * Escape HTML to prevent XSS
     */
    escapeHtml(unsafe) {
        return unsafe
            .replace(/&/g, "&amp;")
            .replace(/</g, "&lt;")
            .replace(/>/g, "&gt;")
            .replace(/"/g, "&quot;")
            .replace(/'/g, "&#039;");
    }

    /**
     * Select a stream
     */
    selectStream(stream) {
        console.log(`Selecting stream: ${stream.name}`);

        const wasPlaying = this.isPlaying;

        if (this.isPlaying) {
            this.audioPlayer.pause();
        }

        this.currentStream = stream;
        this.audioPlayer.src = stream.url;

        this.updateCurrentStreamDisplay();
        this.renderStreams(); // Re-render to update active state
        this.enableControls();

        // Auto-play if we were already playing
        if (wasPlaying) {
            this.playStream();
        }

        this.updateStatus('ready', 'Stream selected');
    }

    /**
     * Delete a stream
     */
    deleteStream(streamId) {
        const stream = this.streams.find(s => s.id === streamId);

        if (!stream) return;

        if (confirm(`Are you sure you want to delete "${stream.name}"?`)) {
            // If this is the current stream, stop playback
            if (this.currentStream && this.currentStream.id === streamId) {
                this.stopStream();
                this.currentStream = null;
                this.updateCurrentStreamDisplay();
                this.disableControls();
            }

            // Remove from streams array
            this.streams = this.streams.filter(s => s.id !== streamId);
            this.saveStreams();
            this.renderStreams();

            console.log(`Deleted stream: ${stream.name}`);
        }
    }

    /**
     * Toggle play/pause
     */
    togglePlayPause() {
        if (!this.currentStream) {
            this.showError('Please select a stream first.');
            return;
        }

        if (this.isPlaying) {
            this.pauseStream();
        } else {
            this.playStream();
        }
    }

    /**
     * Play current stream
     */
    async playStream() {
        if (!this.currentStream) return;

        try {
            this.updateStatus('loading', 'Connecting...');
            await this.audioPlayer.play();
            console.log(`Playing: ${this.currentStream.name}`);
        } catch (error) {
            console.error('Play error:', error);
            this.handlePlayError(error);
        }
    }

    /**
     * Pause current stream
     */
    pauseStream() {
        this.audioPlayer.pause();
        console.log('Stream paused');
    }

    /**
     * Stop current stream
     */
    stopStream() {
        this.audioPlayer.pause();
        this.audioPlayer.currentTime = 0;
        this.audioPlayer.src = '';
        this.isPlaying = false;
        this.updatePlayButton();
        this.updateStatus('stopped', 'Stopped');
        console.log('Stream stopped');
    }

    /**
     * Update volume
     */
    updateVolume(value) {
        const volume = value / 100;
        this.audioPlayer.volume = volume;
        this.volumeValue.textContent = `${value}%`;

        // Save to localStorage
        localStorage.setItem('radioPlayer_volume', value);

        console.log(`Volume set to: ${value}%`);
    }

    /**
     * Adjust volume by delta
     */
    adjustVolume(delta) {
        const currentValue = parseInt(this.volumeSlider.value);
        const newValue = Math.max(0, Math.min(100, currentValue + delta));
        this.volumeSlider.value = newValue;
        this.updateVolume(newValue);
    }

    /**
     * Update volume display
     */
    updateVolumeDisplay() {
        const volume = Math.round(this.audioPlayer.volume * 100);
        this.volumeSlider.value = volume;
        this.volumeValue.textContent = `${volume}%`;
    }

    /**
     * Update play/pause button
     */
    updatePlayButton() {
        const icon = this.playPauseBtn.querySelector('.btn-icon');
        const text = this.playPauseBtn.querySelector('.btn-text');

        if (this.isPlaying) {
            icon.textContent = '‚è∏Ô∏è';
            text.textContent = 'Pause';
        } else {
            icon.textContent = '‚ñ∂Ô∏è';
            text.textContent = 'Play';
        }
    }

    /**
     * Update current stream display
     */
    updateCurrentStreamDisplay() {
        if (this.currentStream) {
            this.currentStreamName.textContent = this.currentStream.name;
            this.currentStreamUrl.textContent = this.currentStream.url;
        } else {
            this.currentStreamName.textContent = 'No stream selected';
            this.currentStreamUrl.textContent = 'Select a stream to begin';
        }
    }

    /**
     * Update status indicator
     */
    updateStatus(type, text) {
        this.statusText.textContent = text;
        this.statusDot.className = `status-dot ${type}`;
        console.log(`Status: ${text}`);
    }

    /**
     * Enable playback controls
     */
    enableControls() {
        this.playPauseBtn.disabled = false;
        this.stopBtn.disabled = false;
    }

    /**
     * Disable playback controls
     */
    disableControls() {
        this.playPauseBtn.disabled = true;
        this.stopBtn.disabled = true;
    }

    /**
     * Update UI state
     */
    updateUI() {
        this.renderStreams();
        this.updateCurrentStreamDisplay();
        this.updatePlayButton();

        if (this.currentStream) {
            this.enableControls();
        } else {
            this.disableControls();
        }
    }

    /**
     * Handle audio errors
     */
    handleAudioError(e) {
        const error = e.target.error;
        let message = 'Audio error occurred';

        if (error) {
            switch (error.code) {
                case error.MEDIA_ERR_ABORTED:
                    message = 'Stream loading was aborted';
                    break;
                case error.MEDIA_ERR_NETWORK:
                    message = 'Network error while loading stream';
                    break;
                case error.MEDIA_ERR_DECODE:
                    message = 'Error decoding audio stream';
                    break;
                case error.MEDIA_ERR_SRC_NOT_SUPPORTED:
                    message = 'Stream format not supported';
                    break;
                default:
                    message = 'Unknown audio error';
            }
        }

        console.error('Audio error:', error);
        this.isPlaying = false;
        this.updatePlayButton();
        this.updateStatus('error', 'Error');
        this.showError(message);
    }

    /**
     * Handle play errors
     */
    handlePlayError(error) {
        let message = 'Failed to play stream';

        if (error.name === 'NotAllowedError') {
            message = 'Please interact with the page before playing audio';
        } else if (error.name === 'NotSupportedError') {
            message = 'This stream format is not supported';
        }

        this.updateStatus('error', 'Play error');
        this.showError(message);
    }

    /**
     * Show error message
     */
    showError(message) {
        this.errorText.textContent = message;
        this.errorMessage.style.display = 'block';

        // Auto-hide after 5 seconds
        setTimeout(() => this.hideError(), 5000);

        console.error('Error:', message);
    }

    /**
     * Show success message (reusing error styling with green color)
     */
    showSuccess(message) {
        this.errorText.textContent = message;
        this.errorMessage.style.display = 'block';
        this.errorMessage.querySelector('.error-content').style.background = '#27ae60';

        // Auto-hide after 3 seconds
        setTimeout(() => this.hideError(), 3000);

        console.log('Success:', message);
    }

    /**
     * Hide error/success message
     */
    hideError() {
        this.errorMessage.style.display = 'none';
        // Reset to error color
        this.errorMessage.querySelector('.error-content').style.background = '#e74c3c';
    }
}

