/**
 * Test script to verify basic radio player functionality structure
 * Run this in a browser console after loading the radio player
 */

console.log("üéµ Testing Radio Stream Player Functionality...");

// Test 1: Check if main elements exist
function testDOMElements() {
    const elements = [
        'audioPlayer',
        'playPauseBtn', 
        'stopBtn',
        'volumeSlider',
        'streamForm',
        'streamsContainer',
        'currentStreamName',
        'statusDot'
    ];

    const missing = elements.filter(id => !document.getElementById(id));

    if (missing.length === 0) {
        console.log("‚úÖ All required DOM elements found");
        return true;
    } else {
        console.error("‚ùå Missing DOM elements:", missing);
        return false;
    }
}

// Test 2: Check if RadioPlayer class is instantiated
function testRadioPlayerInstance() {
    if (window.radioPlayer && window.radioPlayer instanceof RadioPlayer) {
        console.log("‚úÖ RadioPlayer instance created successfully");
        return true;
    } else {
        console.error("‚ùå RadioPlayer not properly instantiated");
        return false;
    }
}

// Test 3: Check if local storage methods work
function testLocalStorage() {
    try {
        const testKey = 'radioPlayer_test';
        localStorage.setItem(testKey, 'test');
        const retrieved = localStorage.getItem(testKey);
        localStorage.removeItem(testKey);

        if (retrieved === 'test') {
            console.log("‚úÖ Local storage functionality working");
            return true;
        } else {
            console.error("‚ùå Local storage read/write failed");
            return false;
        }
    } catch (error) {
        console.error("‚ùå Local storage not available:", error);
        return false;
    }
}

// Test 4: Check if audio element has required methods
function testAudioAPI() {
    const audio = document.getElementById('audioPlayer');

    if (!audio) {
        console.error("‚ùå Audio element not found");
        return false;
    }

    const requiredMethods = ['play', 'pause', 'load'];
    const missing = requiredMethods.filter(method => typeof audio[method] !== 'function');

    if (missing.length === 0) {
        console.log("‚úÖ Audio API methods available");
        return true;
    } else {
        console.error("‚ùå Missing audio methods:", missing);
        return false;
    }
}

// Test 5: Check CSS classes exist
function testCSS() {
    const requiredClasses = [
        'container',
        'control-btn',
        'stream-item',
        'volume-slider',
        'error-message'
    ];

    const missingClasses = [];

    requiredClasses.forEach(className => {
        const elements = document.getElementsByClassName(className);
        if (elements.length === 0) {
            missingClasses.push(className);
        }
    });

    if (missingClasses.length === 0) {
        console.log("‚úÖ All required CSS classes found");
        return true;
    } else {
        console.error("‚ùå Missing CSS classes:", missingClasses);
        return false;
    }
}

// Run all tests
function runAllTests() {
    console.log("\nüß™ Starting Radio Player Tests...\n");

    const tests = [
        { name: "DOM Elements", test: testDOMElements },
        { name: "RadioPlayer Instance", test: testRadioPlayerInstance },
        { name: "Local Storage", test: testLocalStorage },
        { name: "Audio API", test: testAudioAPI },
        { name: "CSS Classes", test: testCSS }
    ];

    const results = tests.map(({ name, test }) => {
        console.log(`Running test: ${name}`);
        const passed = test();
        console.log(`${name}: ${passed ? "PASSED" : "FAILED"}\n`);
        return passed;
    });

    const passedCount = results.filter(r => r).length;
    const totalCount = results.length;

    console.log(`üèÅ Test Results: ${passedCount}/${totalCount} tests passed`);

    if (passedCount === totalCount) {
        console.log("üéâ All tests passed! Radio player should be fully functional.");
    } else {
        console.log("‚ö†Ô∏è Some tests failed. Check the errors above.");
    }

    return passedCount === totalCount;
}

// Export for use in browser
if (typeof window !== 'undefined') {
    window.testRadioPlayer = runAllTests;

    // Auto-run tests if DOM is ready
    if (document.readyState === 'complete') {
        setTimeout(runAllTests, 100);
    } else {
        window.addEventListener('load', () => setTimeout(runAllTests, 100));
    }
}
